mapper.select:
SELECT


	FROM T_GRAB G LEFT JOIN T_GRAB_SIGN S ON G.FSIGN_ID = S.FID
	WHERE G.FID=#{id}

SELECT
FROM T_GRAB G LEFT JOIN T_GRAB_SIGN S ON G.FSIGN_ID = S.FID WHERE G.FID IN
#{id}

SELECT

FROM T_GRAB G LEFT JOIN T_GRAB_SIGN S ON G.FSIGN_ID = S.FID
WHERE 1=1 


		AND G.FURL like CONCAT('%',#{url},'%')




		AND G.FNAME like CONCAT('%',#{name},'%')



	 AND G.FMETHOD_TYPE = #{methodType}



		AND G.FHEAD like like CONCAT('%',#{heads},'%')



	AND G.FSIGN_ID = #{signId}


	AND G.FTOKEN_ID = #{tokenGrabId}



		AND G.FTOKEN_NAME like CONCAT('%',#{tokenName},'%')



	AND G.FTOKEN_POSITION = #{positionEnumInt}


	AND G.FSITE_ID = #{siteId}


	AND G.FCPID = #{cpId}


	AND G.FID=#{id}

ORDER BY G.FID DESC

mapper.insert:
INSERT INTO T_GRAB( FNAME, FURL , FMETHOD_TYPE , FPARAM , FHEAD , FSIGN_ID , FTOKEN_ID , FTOKEN_NAME , FTOKEN_POSITION, FUSERTOKENRULE, FRULE, FORIGINAL_RULE, FSITE_ID, FCPID, FCREATE_TIME , FUPDATE_TIME ) VALUES (
#{name}, #{url}, #{methodType}, #{paramSchema}, #{heads}, #{signId}, #{tokenGrabId}, #{tokenName}, #{positionEnumInt}, #{userTokenRule}, #{grabExtractElement}, #{originalExtract}, #{siteId}, #{cpId}, NOW(), NOW()
)

mapper.delete:
DELETE G

	FROM T_GRAB G
	WHERE G.FID=#{id}
