mapper.select:
SELECT


	FROM T_GRAB_SIGN S
	WHERE S.FID=#{id}

SELECT

FROM T_GRAB_SIGN S
WHERE 1=1

	AND FID=#{id}


	AND FCPID=#{cpId}


	AND FREMARK=#{remark}


	AND FNAME=#{signParamName}


	AND FTYPE=#{signTypeEnum}


	AND FPOSITION=#{positionEnum}


	AND FSIGN_ORDER_TYPE=#{signParamOrderEnum}


	AND FPARAM=#{paramNames}


	AND FSINGLE_PARAM_FORMAT=#{singleParamFormat}


	AND FBEGIN=#{beginCharacter}


	AND FEND=#{endCharacter}


	AND FSPLICE=#{spliceCharacter}


	AND FSIGNKEY=#{signKeyMap}


	AND FHAS_LAST_SPLICE=#{hasLastSplice}

ORDER BY S.FID

mapper.insert:
INSERT INTO T_GRAB_SIGN( FCPID, FREMARK, FNAME , FTYPE , FPOSITION , FSIGN_ORDER_TYPE , FPARAM , FSINGLE_PARAM_FORMAT, FBEGIN , FEND , FSPLICE , FSIGNKEY , FHAS_LAST_SPLICE , FCREATE_TIME , FUPDATE_TIME ) VALUES (
#{cpId}, #{remark}, #{signParamName}, #{signTypeEnum}, #{positionEnum}, #{signParamOrderEnum}, #{paramNames}, #{singleParamFormat}, #{beginCharacter}, #{endCharacter}, #{spliceCharacter}, #{signKeyMap}, #{hasLastSplice}, NOW(), NOW()
)

mapper.delete:
DELETE S

	FROM T_GRAB_SIGN S
	WHERE S.FID=#{id}
